import sys
import json

class Response:
    type = 'json'
    body = ''
    status_code = 200

    def print_result(self):
        body = self.body
        if self.type == 'json':
            try:
                body = json.dumps(body or {})
            except Exception:
                body = ''

        data = json.dumps({'type': self.type, 'body': body, 'status_code': self.status_code})
        sys.stdout.write(data.encode('utf8'))


response = Response()
